mapper.select:
SELECT *
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_PWD = #{memberPwd}
   AND STATUS = 'Y'

mapper.update:
	UPDATE MEMBER
		   SET MEMBER_NAME = #{memberName}
             , GENDER = #{gender}
             , BIRTH_DATE = #{birthDate}
		     , PHONE = #{phone}
		     , ADDRESS = #{address}
		 WHERE MEMBER_ID = #{memberId}
		   AND STATUS = 'Y'

UPDATE MEMBER SET STATUS = 'N'
    WHERE MEMBER_ID = #{memberId}
    AND STATUS = 'Y'

UPDATE MEMBER SET MEMBER_PWD = #{updatePwd}
    WHERE MEMBER_ID = #{memberId}
    AND STATUS = 'Y'

mapper.select:
		INSERT INTO MEMBER(MEMBER_ID
                 , MEMBER_PWD
                 , MEMBER_NAME
                 , GENDER
                 , BIRTH_DATE
                 , EMAIL
                 , PHONE
                 , ADDRESS)
        VALUES(#{memberId}
             , #{memberPwd}
             , #{memberName}
             , #{gender}
             , #{birthDate}
             , #{email}
             , #{phone}
             , #{address})

SELECT MEMBER_ID FROM MEMBER
    WHERE MEMBER_NAME = #{memberName}
      AND BIRTH_DATE = #{birthDate}
      AND EMAIL = #{email}
      AND PHONE = #{phone}
      AND STATUS = 'Y'

mapper.update:
UPDATE MEMBER SET MEMBER_PWD = #{memberPwd}
    WHERE MEMBER_ID = #{memberId}
    AND STATUS = 'Y'

mapper.select:
SELECT EMAIL FROM MEMBER
    WHERE MEMBER_ID = #{memberId}
    AND STATUS = 'Y'
