mapper.select:
SELECT *
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_PWD = #{memberPwd}
   AND STATUS = 'Y'

mapper.update:
	UPDATE MEMBER
		   SET MEMBER_NAME = #{memberName}
             , GENDER = #{gender}
             , BIRTH_DATE = #{birthDate}
             , EMAIL = #{email}
		     , PHONE = #{phone}
		     , ADDRESS = #{address}
		 WHERE MEMBER_ID = #{memberId}
		   AND STATUS = 'Y'

UPDATE MEMBER SET STATUS = 'N'
    WHERE MEMBER_ID = #{memberId}
    AND STATUS = 'Y'

UPDATE MEMBER SET MEMBER_PWD = #{updatePwd}
    WHERE MEMBER_ID = #{memberId}
    AND STATUS = 'Y'
