mapper.select:

 		SELECT COUNT(*)
 		  FROM PRODUCT

	 	   	WHERE PRODUCT_CATEGORY = #{cate}


	 	   	WHERE PRODUCT_CATEGORY = #{cate}


	 	   	WHERE PRODUCT_CATEGORY = #{cate}


	 	   	WHERE PRODUCT_CATEGORY = #{cate}

 		SELECT *
 		  FROM
	 	(SELECT ROWNUM RNUM, A.*
	 	 FROM
	 		(SELECT PRODUCT_NO,
	 			   PRODUCT_NAME,
	 			   PRODUCT_PRICE,
	 			   PRODUCT_CATEGORY,
	 			   PRODUCT_CAPTION,
	 			   PRODUCT_ORIGIN,
	 			   PRODUCT_CHANGE,
	 			   STATUS
	 		FROM PRODUCT

	 	   	WHERE PRODUCT_CATEGORY = #{cate}


	 	   	WHERE PRODUCT_CATEGORY = #{cate}


	 	   	WHERE PRODUCT_CATEGORY = #{cate}


	 	   	WHERE PRODUCT_CATEGORY = #{cate}

	 	   ORDER BY PRODUCT_NO DESC ) A
	 	   )
	 	   WHERE RNUM BETWEEN #{startRow} AND #{endRow}

